DECLARE @row INT = 1,@rowSum AS INt = 0
SELECT * from  #hl_temp

select * from(
select id,sum(varde) as varde,sum(yvarde) as yvarde,sum(varde)*8 + sum(yvarde) as summa from
(
	select id,PATINDEX('B',SUBSTRING(kod,rad,1))*varde as varde,PATINDEX('R',SUBSTRING(kod,rad,1))*varde as yvarde,kod 
	from #hl_temp h
	inner join (
		SELECT 1 as rad,64 as varde
		union all select 2,32
		union all select 3,16
		union all select 4,8
		union all select 5,4
		union all select 6,2
		union all select 7,1
		union all select 8,4
		union all select 9,2
		union all select 10,1
	) as t on 1=1 --where id=2
) t group by id
)t
order by summa

WHILE @row<892
BEGIN
	DECLARE @col INT = 1,@currentMin AS INT = 0,@currentMax AS INT = 127, @currentColMin AS INT = 0,@currentColMax AS INT = 7,@val AS VARCHAR(10)
	WHILE @col<11
	BEGIN     
		SELECT @val = SUBSTRING(kod,@col,1) FROM #hl_temp WHERE id=@row
		
		IF (@val='F') BEGIN
			SET @currentMax=@currentMax -(((@currentMax-@currentMin+1)/2))
		END
		ELSE IF (@val='B') BEGIN
			SET @currentMin=@currentMin+(((@currentMax-@currentMin)/2)+1)
		END 
		IF (@val='L') BEGIN
			SET @currentColMax=@currentColMax-(((@currentColMax-@currentColMin+1)/2))
		END
		IF (@val='R') BEGIN
			SET @currentColMin=@currentColMax-((@currentColMax-@currentColMin)/2)
		END
		SET @col=@col+1
	END
	INSERT INTO #hl_Seats SELECT  (@currentmin*8)+@currentColMin
	SELECT @row=@row+1,@rowSum = CASE WHEN ((@currentmin*8)+@currentColMin)>@rowSum THEN  (@currentmin*8)+@currentColMin ELSE @rowSum END
END
