CREATE TABLE  #hl_bags   (bag VARCHAR(100), recursion int,parent varchar(100), quantity int, RealParent VARCHAR(100))
INSERT INTO #hl_bags select bag,0,bag,1,bag from #hl_temp

DECLARE @recursion INT =0, @rowcount INT =1
WHILE(@rowcount>0) BEGIN
	INSERT INTO #hl_bags
	SELECT  RTRIM(LTRIM(REPLACE(SUBSTRING(value,3,200),'.',''))),recursion+1,h1.parent,SUBSTRING(value,1,2),h1.bag from #hl_bags h1 inner join #hl_temp2 h2 on REPLACE(h2.bag,'bags','bag') =REPLACE(h1.bag,'bags','bag')
	CROSS APPLY STRING_SPLIT(h2.bags, ',')  as temp2 where recursion=@recursion and ISNUMERIC(SUBSTRING(value,1,2))=1
	SELECT @ROWCOUNT=@@ROWCOUNT,@recursion=@recursion+1
END

