;WITH Bags AS
(
    SELECT n = 1,quantity as qty,bag as bag,CAST(bag as VARCHAR(1000)) as tag
	from #hl_bags h1 
    where h1.realparent like '%shiny gold%' and h1.recursion=1 
    UNION ALL
	select  n + 1,qty*ISNULL(h1.quantity,0),h1.bag,CAST(h1.bag+tag as VARCHAR(1000))
	from Bags inner join #hl_bags h1  on  REPLACE(numbers.bag,'bags','bag') =REPLACE(h1.realparent,'bags','bag') and h1.recursion=n+1 and  h1.parent like '%shiny gold%'
	AND n+1 <= 10
)
SELECT SUM(qty) FROM (SELECT distinct tag,qty FROM Bags) tmp



